class ReportDescription < Cause
	belongs_to :report,foreign_key:"owner_id",class_name:"Report"

	def self.get_headers
		[
			{:title=>"Category",:field=>"category"},
			{:title=>"Attribute",:field=>"get_attr"},
			{:title=>"Value",:field=>"get_value"}
		]
	end
	
	def get_value
		if self.value=="true"
			"Yes"
		else
			self.value
		end
	end

